    private void agregarVActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_agregarVActionPerformed
        // Boton agregar a vestido 
        estructura.agregarVestido(Double.parseDouble(alturaV.getText()), Double.parseDouble(medidaV.getText()), colorV.getText(), (String) tallaV.getSelectedItem(), Double.parseDouble(precioV.getText()));
        mensajePantalla.setText(estructura.imprimirA());
        alturaV.setText("");
        medidaV.setText("");
        colorV.setText("");
        precioV.setText("");
    }//GEN-LAST:event_agregarVActionPerformed

    private void agregarPActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_agregarPActionPerformed
        // Boton agregar pantalon
        estructura.agregarPantalon(generoP.getText(), ocasionP.getText(), colorP.getText(), (String) tallaP.getSelectedItem(), Double.parseDouble(precioP.getText()));
        mensajePantalla.setText(estructura.imprimirA());
        generoP.setText("");
        ocasionP.setText("");
        colorP.setText("");
        precioP.setText("");
    }//GEN-LAST:event_agregarPActionPerformed

    private void agregarCActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_agregarCActionPerformed
        // Boton agregar camisa
        estructura.agregarCamisa(tmangaC.getText(), Integer.parseInt(nbolsillosC.getText()), colorC.getText(), (String) tallaC.getSelectedItem(), Double.parseDouble(precioC.getText()));
        mensajePantalla.setText(estructura.imprimirA());
        tmangaC.setText("");
        nbolsillosC.setText("");
        colorC.setText("");
        precioC.setText("");
    }//GEN-LAST:event_agregarCActionPerformed

    private void eliminarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_eliminarActionPerformed
        // Boton eliminar
        estructura.eliminarA(Integer.parseInt(indiceE.getText()));
        mensajePantalla.setText(estructura.imprimirA());
        indiceE.setText("");
    }//GEN-LAST:event_eliminarActionPerformed

    private void salirActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_salirActionPerformed
        // Boton cerrar 
        System.exit(0);
    }//GEN-LAST:event_salirActionPerformed

